let oldName = $("#nick").val();
let nickDegistir = false;

function isimDegistirA(elmnt) {

    let nick = $("#nick");
    let oldName = nick.val();
    let newName = prompt("Yeni isim giriniz.", oldName)
    if (!(newName == "" || newName == undefined)) {
        nickDegistir = true;
        $(elmnt).parent().css({
            "background": "red"
        })
        nick.val(newName);
        onClickPlay();
    }

}

function isimDegistirD(elmnt) {
    if (nickDegistir) {
        $(elmnt).parent().css({
            "background": "#fff"
        })
        $("#nick").val(oldName);
        onClickPlay();
    } else {
        alert("Daha önce hiç nick girmediniz.");
    }

}
