let nickNames = [];
let skinNames = [];
let floodNames = [];
let degisimNick;
let degisimSkin;
let degisimFlood;
let floodAktif = false;

let degisimNickNumber = 0;
let degisimSkinNumber = 0;
let degisimFloodNumber = 0;

let nickSuresi = 0;
let skinSuresi = 0;
let floodSuresi = 0;

function textBoxAdd(elmnt) {
    let textboxNumber = $(elmnt).val();
    let textBoxAll = "";
    $("#textBoxs").children().remove();
    for (var i = 1; i <= textboxNumber; ++i) {
        textBoxAll += `<label for='loopNickName${i}'>Nick ${i}</label><input type='textbox' id='loopNickName${i}' class='form-control textName'>`
    }
    textBoxAll += "<button id='save' onclick='saveInformation(`" + $("#nameX").text() + "`)'>Kaydet ve kapat</button>"
    $("#textBoxs").append(textBoxAll);
}

function saveInformation(valueX) {
    this.valueX = valueX;
    console.log(this.valueX);
    if (this.valueX == "nick") {
        nickNames = [];
        degisimNickNumber = 0;
        nickSuresi = $("#nickTime").val() * 1000 == 3000 ? 3400 : $("#nickTime").val() * 1000;
        degisimNick = setInterval(nickDegisimStart, nickSuresi);
    } else if (this.valueX == "skin") {
        skinNames = [];
        degisimSkinNumber = 0;
        skinSuresi = $("#skinTime").val() * 1000 == 3000 ? 3400 : $("#skinTime").val() * 1000;
        degisimSkin = setInterval(skinDegisimStart, skinSuresi);
    } else if (this.valueX == "flood") {
        $("#chat_textbox").hide();
        floodAktif = true;
        floodNames = [];
        degisimFloodNumber = 0;
        floodSuresi = $("#floodTime").val() * 1000 == 3000 ? 3700 : $("#floodTime").val() * 1000;
        degisimFlood = setInterval(floodDegisimStart, floodSuresi);
    }
    $(".textName").each(function () {
        if (valueX == "nick")
            nickNames.push($(this).val())
        else if (valueX == "skin")
            skinNames.push($(this).val())
        else if (valueX == "flood")
            floodNames.push($(this).val())
    });

    if ($("#playerLoop"))
        $("#playerLoop").remove();
}

function nickDegisimStart() {
    $("#nick").val(nickNames[degisimNickNumber]);
    onClickPlay();
    degisimNickNumber++;
    if (degisimNickNumber == nickNames.length)
        degisimNickNumber = 0;
}

function skinDegisimStart() {
    $("#txtSkin").val(skinNames[degisimSkinNumber]);
    onClickPlay();
    degisimSkinNumber++;
    if (degisimSkinNumber == skinNames.length)
        degisimSkinNumber = 0;
}
let surekliX;

function enter() {
    if (floodAktif) {
        $("#chat_textbox").trigger($.Event("keydown", {
            keyCode: 13
        }));
        $("#chat_textbox").trigger($.Event("keyup", {
            keyCode: 13
        }));
    }
}

function floodDegisimStart() {

    $("#chat_textbox").val(floodNames[degisimFloodNumber]);
    surekliX = setInterval(enter, 1000);
    if (!surekliX)
        clearInterval(surekliX);
    degisimFloodNumber++;
    if (degisimFloodNumber == floodNames.length)
        degisimFloodNumber = 0;

}

