/* HIZLI MESAJ GÖNDER */
let hizliMesajPanel = false;

function hizliMesajGonderA(elmnt) {
    $(elmnt).parent().css({
        "background": "red"
    });
    if (!hizliMesajPanel) {
        hizliMesajPanel = true;
        hizliMesajDiv = $("<div class='hizliMesajArea'>");
        for (var i = 0; i < hizliMesajOptions.length; ++i) {
            hizliMesajDiv.append($("<div clas='hizliMesajItem'>").html("<span style='color:#FF342D'>Kısayol:[" + hizliMesajOptions[i].gercekKisayolNumber + "]  ---  </span>" + hizliMesajOptions[i].name))
            if (i == (hizliMesajOptions.length - 1))
                $("body").append(hizliMesajDiv)

        }
        $(".hizliMesajArea").fadeIn();

    } else {
        $(".hizliMesajArea").fadeIn();
    }

}

$(document).on('keydown', function (e) {
    if (hizliMesajPanel) {
        var key = e.which || e.keyCode;
        hizliMesajOptions.forEach(element => {
            if (element.kisayolNumber == key && $('#chat_textbox:focus').size() == 0) {
                $("#chat_textbox").val("");
                $("#chat_textbox").val(element.chatName);
                $("#chat_textbox").focus();
                $("#chat_textbox").trigger($.Event("keydown", {
                    keyCode: 13
                }));
                $("#chat_textbox").trigger($.Event("keyup", {
                    keyCode: 13
                }));
            }
        });
    }
});


var hizliMesajGonderD = function (elmnt) {
    $(elmnt).parent().css({
        "background": "#fff"
    });
    hizliMesajPanel = false;
    $(".hizliMesajArea").remove();

}
